mapper.select:
SELECT *
FROM tb_corr_status


		AND equipment_name = #{equipment_name}



		AND replacement_date >=
		#{startDate}


		AND replacement_date <=
		#{endDate}


ORDER BY tdatetime DESC

SELECT * FROM tb_part_temp WHERE 1 = 1
AND `date` LIKE CONCAT(#{startDate}, '%')
AND mch_code = #{mch_code}

mapper.update:
UPDATE tb_part_temp
specific_gravity = #{value}, visocosity = #{value}, tank_temp = #{value}, chiller_temp = #{value},
WHERE id = #{id}

mapper.select:
SELECT *
FROM tb_machineliquidmanage
WHERE 1 = 1

	AND in_date = #{in_date}


	AND mch_name = #{mch_name}

SELECT *
FROM tb_machineliquidmanage_2
WHERE 1=1

	AND mch_name = #{mch_name}


	AND in_date = #{in_date}

ORDER BY ck_time DESC

SELECT *
FROM tb_standard_doc


    AND mch_name = #{mch_name}



    AND cr_date BETWEEN #{startDate} AND #{endDate}
